package com.jeefw.service.sys.impl;import com.jeefw.dao.sys.AttachmentDao;import com.jeefw.model.sys.Attachment;import com.jeefw.service.sys.AttachmentService;import core.service.BaseService;import java.util.List;import javax.annotation.Resource;import org.springframework.stereotype.Service;@Servicepublic class AttachmentServiceImpl  extends BaseService<Attachment>  implements AttachmentService{  private AttachmentDao attachmentDao;    @Resource  public void setAttachmentDao(AttachmentDao attachmentDao)  {    this.attachmentDao = attachmentDao;    this.dao = attachmentDao;  }    public List<Object[]> queryFlowerList(String epcId)  {    return this.attachmentDao.queryFlowerList(epcId);  }    public void deleteAttachmentByForestryTypeId(Long umTypeId)  {    this.attachmentDao.deleteAttachmentByForestryTypeId(umTypeId);  }    public List<Object[]> querySensorList()  {    return this.attachmentDao.querySensorList();  }}