package core.web;import java.beans.PropertyEditorSupport;import java.text.DateFormat;import java.text.ParseException;import java.text.SimpleDateFormat;public class CustomDateEditor  extends PropertyEditorSupport{  public static final String DEFAULT_DATE_FORMAT = "yyyy-MM-dd";  public static final String DEFAULT_DATETIME_FORMAT = "yyyy-MM-dd HH:mm:ss";  public static final DateFormat[] ACCEPT_DATE_FORMATS = { new SimpleDateFormat("yyyy-MM-dd HH:mm:ss"), new SimpleDateFormat("yyyy-MM-dd") };    public void setAsText(String text)    throws IllegalArgumentException  {    if ((text == null) || (text.trim().equals(""))) {      setValue(null);    }    DateFormat[] arrayOfDateFormat;    int j = (arrayOfDateFormat = ACCEPT_DATE_FORMATS).length;    for (int i = 0; i < j; i++)    {      DateFormat format = arrayOfDateFormat[i];      try      {        setValue(format.parse(text));        return;      }      catch (ParseException e) {}catch (RuntimeException localRuntimeException) {}    }  }    public String getAsText()  {    return (String)getValue();  }}