package core.web;import com.jeefw.model.sys.SysUser;public class SessionThreadLocal{  private static ThreadLocal<SysUser> ADMINUSERTHREADLOCAL = new ThreadLocal();    public static SysUser getThreadSysUser()  {    return (SysUser)ADMINUSERTHREADLOCAL.get();  }    public static void setThreadSysUser(SysUser sysUser)  {    ADMINUSERTHREADLOCAL.set(sysUser);  }}